class Node:
    def __init__(self, val):
        self.val = val
        self.children = []

def find_ancestor(root, node1, node2):
    if root is None:
        return None

    if root.val == node1 or root.val == node2:
        return root

    ancestors = set()

    def dfs(node, ancestors):
        if node is None:
            return

        for child in node.children:
            if child.val in (node1, node2):
                ancestors.update(dfs(child, ancestors))
            else:
                dfs(child, ancestors)

    dfs(root, ancestors)

    for node in ancestor:
        if node.val == node1 or node.val == node2:
            return node

    return None
